import React from "react";
import styled from "styled-components";
import { BaseContainer } from "../../helpers/layout";
import { getDomain } from "../../helpers/getDomain";
import { withRouter } from "react-router-dom";
import { Button } from "../../views/design/Button";
import Link from "react-router-dom/es/Link";

const FormContainer = styled.div`
  margin-top: 2em;
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 300px;
  justify-content: center;
`;

const Form = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 60%;
  height: 375px;
  font-size: 16px;
  font-weight: 300;
  padding-left: 37px;
  padding-right: 37px;
  border-radius: 5px;
  background: linear-gradient(rgb(27, 124, 186), rgb(2, 46, 101));
  transition: opacity 0.5s ease, transform 0.5s ease;
`;

const InputField = styled.input`
  &::placeholder {
    color: rgba(255, 255, 255, 0.2);
  }
  height: 35px;
  padding-left: 15px;
  margin-left: -4px;
  border: none;
  border-radius: 20px;
  margin-bottom: 20px;
  background: rgba(255, 255, 255, 0.2);
  color: white;
`;

const Label = styled.label`
  color: white;
  margin-bottom: 10px;
  text-transform: uppercase;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

/**
 * Classes in React allow you to have an internal state within the class and to have the React life-cycle for your component.
 * You should have a class (instead of a functional component) when:
 * - You need an internal state that cannot be achieved via props from other parent components
 * - You fetch data from the server (e.g., in componentDidMount())
 * - You want to access the DOM via Refs
 * https://reactjs.org/docs/react-component.html
 * @Class
 */
class Login extends React.Component {
  /**
   * If you don’t initialize the state and you don’t bind methods, you don’t need to implement a constructor for your React component.
   * The constructor for a React component is called before it is mounted (rendered).
   * In this case the initial state is defined in the constructor. The state is a JS object containing two fields: name and username
   * These fields are then handled in the onChange() methods in the resp. InputFields
   */
  constructor(props) {
    super(props);
    this.state = {
      username: null,
      password: null,
      user: null,
      history:this.props.history,
    };
  }


    login = () => {
        //use json() method to extract JSON body from response
        //if status 404, no matching user and password found
        const status = response =>{
            if(response.status === 200){
                //Syntax reminder: Promise.resolve(value);
                //value: 
                return Promise.resolve(response);
            }
            alert("Login failed. Please check your inputs.");
            //Syntax reminder: Promise.reject(reason);
            return Promise.reject(new Error(response.statusText));
        };

        const apistr = response => response.json();
        //fetch login endpoint
        fetch(`${getDomain()}/login`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password
            })
        })

            //syntax reminder then(function) {}
            .then(status)
            .then(apistr)
            .then(response =>{
                // debugging
                console.log('Data received', response);
                // set user to data
                this.setState({user: response});
                // debugging
                console.log('Found user ', this.state.user);
                // set token and id in local storage (why is session storage not working?)
                localStorage.setItem("token", response.token);
                localStorage.setItem("userId", response.id);
                //push to game
                this.props.history.push('game');
            })

            .catch(error => {
                console.log(error);
            })


    }


  /**
   * HTTP POST request to backend with credentials
   * Expected answer: credentials accepted or denied


  login = () => {
    //changed from users to login according to mapping in backend
    fetch(`${getDomain()}/login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
          "Accept": "application/json"
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password,
      })
    })

      .then(response => {
        if (response.status === 200) {
          var dbuser = response.json();
          const user = new User(dbuser);
          this.setState({user: response});
          localStorage.setItem("token", response.token);
          localStorage.setItem("userId", response.id);
          console.log(localStorage);
          alert("You are logged-in.");
          this.props.history.push('game/dashboard');
        } else {
          alert("Login failed.")
        }

      })
        .catch(err => {
        if (err.message.match(/Failed to fetch/)) {
          alert("The server cannot be reached. Did you start it?");
        } else {
          alert(`Something went wrong during the login: ${err.message}`);
        }
      });
  };*/

  /**
   *  Every time the user enters something in the input field, the state gets updated.
   * @param key (the key of the state for identifying the field that needs to be updated)
   * @param value (the value that gets assigned to the identified state key)
   */
  handleInputChange(key, value) {
    // Example: if the key is username, this statement is the equivalent to the following one:
    // this.setState({'username': value});
    this.setState({ [key]: value });
  }

  /**
   * componentDidMount() is invoked immediately after a component is mounted (inserted into the tree).
   * Initialization that requires DOM nodes should go here.
   * If you need to load data from a remote endpoint, this is a good place to instantiate the network request.
   * You may call setState() immediately in componentDidMount().
   * It will trigger an extra rendering, but it will happen before the browser updates the screen.
   */
  componentDidMount() {}

  render() {
    return (
      <BaseContainer>
        <FormContainer>
          <Form>
            <Label>Username</Label>
            <InputField
              placeholder="Enter here.."
              onChange={e => {
                this.handleInputChange("username", e.target.value);
              }}
            />
            <Label>Password</Label>
            <InputField
              placeholder="Enter here.."
              onChange={e => {
                this.handleInputChange("password", e.target.value);
              }}
            />
            <ButtonContainer>
              <Button
                disabled={!this.state.username || !this.state.password}
                width="50%"
                onClick={() => {
                  this.login();
                }}
              >
                Login
              </Button>
            </ButtonContainer>
            <Link to="/register">
            <ButtonContainer>

              <Button
                  width="50%"
              >
                Register
              </Button>
            </ButtonContainer>
            </Link>
          </Form>
        </FormContainer>
      </BaseContainer>
    );
  }
}

/**
 * You can get access to the history object's properties via the withRouter.
 * withRouter will pass updated match, location, and history props to the wrapped component whenever it renders.
 */
export default withRouter(Login);
